        required
      }
    }
  },
  computed: {
    ...mapGetters(['getRooms', 'getTasks', 'getTaskByID', 'getRefreshInterval'])
  },
  mounted() {
    if (this.getRefreshInterval) {
      console.log('clean');
      this.$store.commit('clearRefreshInterval');
